<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Dashboard extends CI_Controller {    public $data = array();    function __construct() {        parent::__construct();                ## Set Page Details        $this->data["page_title"] = "Dashboard | ".PROJECTNAME;        $this->data["page_keyword"] = PROJECTNAME;        $this->data["page_desc"] = PROJECTNAME;        $this->load->library('Session');        $this->load->model("auth_model", "auth", TRUE);                ## Set default Items        $this->auth->isLogin();    }    public function index() {        $this->auth->isLogin();        $this->data["countuser"] = 11; //$this->dashboard->countUser();        $this->data["menu_active"] = "dashboard";        $this->load->view('admin/dashboard/dashboard_view', $this->data);    }}